gsap.registerPlugin(ScrollTrigger);let tl=gsap.timeline({defaults:{ease:"power4.out"}});tl.from(".main-nav ul li a",{opacity:0,y:-50,duration:1,stagger:.2}),tl.from(".btn-secondary",{x:100,opacity:0,rotation:360,duration:.8},"-=0.5"),tl.from(".btn-primary",{x:100,opacity:0,duration:.8},"<");const animateCounters=()=>{const t=document.querySelectorAll(".counter"),e=2e3;t.forEach(t=>{const n=+t.getAttribute("data-target");let s=null;const o=i=>{s||(s=i);const a=i-s,c=Math.min(a/e,1),r=Math.floor(c*n);n===10?t.innerText=r+"M+":t.innerText=r+"+",a<e?requestAnimationFrame(o):t.innerText=n+(n===10?"M+":"+")};requestAnimationFrame(o)})},initMarquee=()=>{const e=document.getElementById("customersMarquee");if(!e)return;e.innerHTML+=e.innerHTML;const t=gsap.to(e,{x:"0",startAt:{x:"-50%"},duration:30,ease:"none",repeat:-1});e.addEventListener("mouseenter",()=>t.pause()),e.addEventListener("mouseleave",()=>t.play())};document.addEventListener("DOMContentLoaded",()=>{animateCounters(),initMarquee();const e=document.querySelector(".site-header");e&&ScrollTrigger.create({start:"top -2",onEnter:()=>e.classList.add("scrolled"),onLeaveBack:()=>e.classList.remove("scrolled")})}),document.addEventListener("visibilitychange",()=>{document.visibilityState==="visible"&&tl.restart()})